import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { Router } from '@angular/router';
import { Observable } from 'rxjs';
import { employee } from '../model/employee.model';
import { EmployeeserviceService } from '../services/employeeservice.service';

@Component({
  selector: 'app-employee-list',
  templateUrl: './employee-list.component.html',
  styleUrls: ['./employee-list.component.scss']
})
export class EmployeeListComponent implements OnInit {
    // public Employeelist$:Observable<any>
    public Employeelist:any

    @Output() editdata= new EventEmitter();
  constructor(
    private route:Router,
    private employeeservices:EmployeeserviceService
  ) {
    this.editdata=new EventEmitter();

    // this.Employeelist$=this.employeeservices.getEmploye()
  }

  ngOnInit(): void {
    this.getEmployeeList()
  }

  getEmployeeList(){
    this.employeeservices.getEmploye().subscribe((employee:employee[])=>{
      this.Employeelist=employee
    })
  }
  //  nagivate router fuction
  emppyeeform(){
    this.route.navigate(['employee/add'])
  }
  // edite employee function
  onEdit(id:number,item:any){
    // this.editdata.emit(item)
    this.route.navigate(['employee/edit',+id],{ queryParams:item })
  }
  // Delete employee function
  onDelete(id:number){
    this.employeeservices.deleteEmployee(id).subscribe((Response)=>{
      // this.getEmployeeList();
    })
  }
  // show Details in employee
  showDetails(item:any){
    this.route.navigate(['home'],{ queryParams:item })

  }

}
<button class=" btn btn-primary" (click)="emppyeeform()">ADD EMPLOYEE</button>
<h3 class="mt-4">EMPLOYEE DETAILS</h3>
<table class="table mt-3">
    <thead>
      <tr>
        <th scope="col">Id</th>
        <th scope="col">Firstname</th>
        <th scope="col">LastName</th>
        <th scope="col">Email</th>
        <th scope="col">Phone No</th>
        <th scope="col">Address</th>
        <th scope="col">City</th>
        <th scope="col">state</th>
        <th scope="col">PinCode</th>
        <th>Action</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let employee of Employeelist$ | async">
        <td>{{employee.id}}</td>
        <td>{{employee.firstname}}</td>
        <td>{{employee.lastname}}</td>
        <td>{{employee.email}}</td>
        <td>{{employee.phoneno}}</td>
        <td>{{employee.address}}</td>
        <td>{{employee.city}}</td>
        <td>{{employee.state}}</td>
        <td>{{employee.pincode}}</td>
        <td>
            <button  class=" btn btn-success" (click)="onEdit(employee.id,employee)">Edit</button>
            <button class=" btn btn-danger ms-2" (click)="onDelete(employee.id)">Delete</button>
            <button class=" btn btn-primary ms-2" (click)="showDetails(employee)">Details</button>
        </td>
      </tr>

    </tbody>
  </table>
